    @Test
    public void testRegexGroup() throws Exception {
        String orginValue = "${USER_ID}_${TOPIC_NAME}_${intention.entity}";
        Matcher matcher = JSON_PATTERN.matcher(orginValue);
        while (matcher.find()) { // 需要替换
            String fullName = matcher.group("fullName");
            String keyName = matcher.group("keyName");
            System.out.println("fullName:"+fullName + " keyName:"+ keyName);
        }
        System.out.println("over");
    }


